mapper.select:
  		select o.*,e.Employee_Name,p.type_name from orders o,employees e,photo_type p
  		 where o.type_id=p.type_id and o.floor=e.Employee_ID
  		
  			and o.date= #{date}
  		
  		
			and o.customer like #{customer}  		
  		
  		
  			and o.order_Id=#{orderId}

  	select  IFNULL(SUM(o.front_cost),0) from orders o,employees et
	where o.flag="是"  and et.Employee_ID=o.floor
	and o.floor=#{empId}  and o.date like #{testDate}

  	select  IFNULL(SUM(o.second_cost),0) from orders o,employees et
	where o.flag="是"  and et.Employee_ID=o.make_Up
	and o.make_Up=#{empId}  and o.date like #{testDate}

  	select  IFNULL(SUM(o.second_cost),0) from orders o,employees et
	where o.flag="是"  and et.Employee_ID=o.photo
	and o.photo=#{empId}  and o.date like #{testDate}

  	select  IFNULL(SUM(o.second_cost),0) from orders o,employees et
	where o.flag="是"  and et.Employee_ID=o.ps
	and o.ps=#{empId}  and o.date like #{testDate}

  	select  IFNULL(SUM(o.second_cost),0) from orders o,employees et
	where o.flag="是"  and et.Employee_ID=o.choose
	and o.choose=#{empId}  and o.date like #{testDate}
